  // CSV出力機能
  function exportToCSV() {
    if (measurementLog.length === 0) {
      alert('出力するデータがありません');
      return;
    }
    const headers = ['No', '時刻', '耳', 'トランスデューサー', '周波数(Hz)', '閾値(dB)', 'マスキング', 'マスキングレベル(dB)', 'Scale-Out'];
    const csvContent = [headers.join(','), ...measurementLog.map((log, index) => [index + 1, log.timestamp, log.ear, log.transducer, log.freq, log.dB, log.masked ? 'ON' : 'OFF', log.masked ? log.maskLevel : '-', log.so ? 'Yes' : 'No'].join(','))].join('\n');
    const blob = new Blob([csvContent], {
      type: 'text/csv;charset=utf-8;'
    });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', `audiogram_measurement_${new Date().toISOString().split('T')[0]}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

